var VIA_DEVICE_NAME = "MQTT Raspberry Pi with ENER314-RT"; 
var UNIQUE_ID_SUFFIX = "_energenie_mqtt"; 
var EXPIRE_AFTER = "60";  //Number of seconds after the sensorâ€™s state expires
var STATE_TOPIC = "energenie_gateway/energeniemqtt_" + msg.payload.deviceId + "/state";
var SET_TOPIC = "energenie_gateway/energeniemqtt_" + msg.payload.deviceId + "/set";

// Code reuse to save typing it out for each config.
var AVAILABILITY = [
    {
        topic: "energenie_gateway/bridge/state",
        payload_available: "online",
        payload_not_available: "offline"
    }
];
var DEVICE = {
    name: msg.payload.deviceId,
    identifiers: IDENTIFIERS_PREFIX + msg.payload.deviceId,
    configuration_url: CONFIGURATION_VAR,
    manufacturer: MANUFACTURER_NAME,
    model: MODEL_NAME,
    via_device: VIA_DEVICE_NAME
};


// Measured power sensor in watts.
var config1 = {
    payload: {
        // availability_mode: "all",  // Commented out as we are using only one topic at the moment.
        availability: AVAILABILITY,
        device: DEVICE,
        // enabled_by_default: true,
        // entity_category: "none",
        expire_after: EXPIRE_AFTER,
