    via_device: VIA_DEVICE_NAME
};

// Control and eTRV
var config1 = {
    payload: {
        // availability_mode: "all",  // Commented out as we are using only one topic at the moment.
        availability: AVAILABILITY,
        device: DEVICE,
        // entity_category: "none",
        // expire_after: EXPIRE_AFTER, // never expire
        // retain: true,
        name: msg.payload.deviceId + " Radiator",
        unique_id: msg.payload.deviceId + "_trv",
        temperature_unit: "C",

        //Limit the climate modes to just heat (no fan, cool, etc)
        // If this would ever chnage then the forced "heat" mode in the pre-node would need to be removed.
        // Could add an off mode, which closes the valve. But this is not how energenie did it on their app.
        // modes: ["off","heat"],
        // modes: ["heat"],

        modes: "heat",
        mode_state_topic: STATE_TOPIC,
        mode_state_template: "{{ value_json.mode }}", // Fake mode manually added
        optimistic: "true", // Enforce optimistic mode, to hide the 5 min delay for setpoint command delays from the frontend  
        temp_step: "0.5", // Step size for temperature set point.

        // The MQTT topic on which to listen for the current temperature. (int) NOTE: The VALVE_STATE must be set to 'Auto' for this to work.
        current_temperature_topic: STATE_TOPIC,
        current_temperature_template: "{{ value_json.TEMPERATURE }}",

        // MQTT topic to subscribe for changes in the target temperature
        temperature_state_topic: STATE_TOPIC,
        temperature_state_template: "{{ value_json.TARGET_TEMP }}",

        // The MQTT topic to publish commands to change the target temperature.
        temperature_command_topic: "energenie_gateway/energeniemqtt_" + msg.payload.deviceId + "/set",
        temperature_command_template: '{ "command":244, "data":{{value}} }', // command = 244 is for setting the target temperature, so I DON'T need to edit the passed data back from MQTT

        // Temp Range as per manual
        max_temp: 30,
        min_temp: -4

    },
    topic: "homeassistant/climate/energeniemqtt_" + msg.payload.deviceId + "/trv/config"
};

